    <script>
        var connection = new signalR.HubConnectionBuilder().withUrl(""/chathub"").build();

        connection.start();
        console.log(connection);

        $(""#sendMsg"").click(function () {

            var name = $(""#Name"").innerHTML
            var message = $(""#messageInput"").val();

            //alert(`${name} says: ${message}`);

            connection.invoke(""SendMessage"", name, message);
        });

        ///////////////////////////////////////////
        $("".sendPrivate"").click(function () {
            var name = $(""#Name"").innerHTML
            var message = $(""#messageInput"").val();

            var connectionId = $("".getConnection"").attr(""data-id"");
            //alert(connectionId)
            connection.invoke(""SendPrivateMessage"", name, message, connectionId)

        })
        //////////////////////////////////////////
        connection.on(""ReceiveSpecificMessage"", function (name, message ) {

            var li = document.createElement(""l");
                WriteLiteral(@"i"")
            var chatBox = document.getElementById(""chat"");
            chatBox.appendChild(li);
            li.innerHTML = `${name} : ${message}`
        })





        connection.on(""ReceiveMessage"", function (name, message, date) {
            var li = document.createElement(""li"")

            var chatBox = document.getElementById(""chat"");

            chatBox.appendChild(li);
            li.innerHTML = `${name} : ${message}  ${date}`
        })

        connection.on(""UserConnected"", function (id) {

            let element = document.getElementById(id);
            $(element).find(""span"").removeClass(""bc-offline"")
            $(element).find(""span"").addClass(""bc-online"")
        })
        connection.on(""UserDisConnected"", function (id) {

            let element = document.getElementById(id);
            $(element).find(""span"").removeClass(""bc-online"")
            $(element).find(""span"").addClass(""bc-offline"")

        })

    </script>
